import Layout from '../Components/layout';
import {Button} from 'react-bootstrap';


const Launch = ({launch })=> {
    const dataList = data;
    const LaunchTrue = launch;
          return(
        <Layout>
        <div>
       <h1>Welcome to Space X Programs </h1> 
      
       <Display passedData ={dataList} launch ={LaunchTrue} />
        </div>
       
        </Layout>
          )
    }

function launchSuccess(){
    Launch.getInitialProps = async (ctx)=>{
        alert("Inside Launch Success")
        const launch = await fetch('https://api.spacexdata.com/v3/launches?limit=100&amp;launch_success=true');
        const dataLaunch = await launch.json();  
        return { launch: dataLaunch }
    
    }
    
}

const About = ()=> (
    <Layout>
   
    <div>
       
       <h2> You can use the below filters </h2>
    
    <Button style={{margin:'10px'}} onClick={launchSuccess}>Only Successful Launch</Button>
    <Button>2012</Button>
    
    </div>
  
    </Layout>
);

export default About;
