import Layout from '../Components/layout';
import Fetch from '../components/Layout';

import Display from '../Components/Display';
import { useRouter } from 'next/router';
import { useState } from 'react';
import Filter from '../Components/filter';
import{Dropdown,DropdownButton} from 'react-bootstrap'


// const router = useRouter()

const Index = ({ data,launch })=> {
  const [value,setValue]=useState('');
  const handleSelect=(e)=>{
    console.log(e);
    setValue(e)
  }
    const dataList = data;
    const LaunchTrue = launch;
          return(
        <Layout>
        <div>
       <h1>Welcome to Space X Programs </h1> 
      
       <Display passedData ={dataList} launch ={LaunchTrue} />
      
        </div>
        <DropdownButton
      alignRight
      title="Dropdown right"
      id="dropdown-menu-align-right"
      onSelect={handleSelect}
        >
              <Dropdown.Item eventKey="option-1">option-1</Dropdown.Item>
              <Dropdown.Item eventKey="option-2">option-2</Dropdown.Item>
              <Dropdown.Item eventKey="option-3">option 3</Dropdown.Item>
              <Dropdown.Divider />
              <Dropdown.Item eventKey="some link">some link</Dropdown.Item>
      </DropdownButton>
      <h4>You selected {value}</h4>
       
        </Layout>
          )
    }
   

Index.getInitialProps = async (ctx)=>{
    const res = await fetch(`https://api.spacexdata.com/v3/launches?limit=100&launch_success`);
    //console.log("State in index.js" + this.state.sLaunch)
    // if(this.state.sLaunch == true){
    //   res = await fetch('https://api.spacexdata.com/v3/launches?limit=100&launch_success=true');
    // }
    const data = await res.json();  
    return { data: data }

}




  
export default Index;
